mapper.select:
SELECT COUNT(1)
FROM favorites
WHERE user_id = #{userId}
AND type = #{type}
AND reference_id = #{referenceId}

mapper.delete:
DELETE FROM favorites
WHERE user_id = #{userId}
AND type = #{type}
AND reference_id = #{referenceId}

mapper.select:
SELECT reference_id
FROM favorites
WHERE user_id = #{userId}
AND type = #{type}
