mapper.select:
SELECT COUNT(1)
FROM favorites
WHERE user_id = #{userId}
AND type = #{type}
AND reference_id = #{referenceId}

mapper.delete:
DELETE FROM favorites
WHERE user_id = #{userId}
AND type = #{type}
AND reference_id = #{referenceId}

mapper.select:
SELECT reference_id
FROM favorites
WHERE user_id = #{userId}
AND type = #{type}

SELECT DISTINCT p.apt_seq
FROM favorites f
JOIN properties p ON p.property_id = f.reference_id
WHERE f.user_id = #{userId}
AND f.type = #{type}
