mapper.select:
SELECT pi.apt_seq AS aptSeq, pi.apt_nm AS aptName, CONCAT(pi.road_nm, ' ', IFNULL(pi.road_bun, '')) AS roadAddress, ROUND(pi.property_rating, 1) AS propertyRating, img.image_url AS imageUrl FROM property_infos pi
LEFT JOIN ( SELECT apt_seq, MAX(image_url) AS image_url FROM property_image WHERE is_main = 1 GROUP BY apt_seq ) img ON img.apt_seq = pi.apt_seq
ORDER BY propertyRating DESC LIMIT #{limit}

SELECT DISTINCT SUBSTRING(dong_code, 1, 5) AS sgg_cd
FROM dongcodes
WHERE sido_name = #{sido}
AND gugun_name = #{gugun}
LIMIT 1

SELECT pi.apt_seq AS aptSeq, pi.apt_nm AS aptName, CONCAT(pi.road_nm, ' ', IFNULL(pi.road_bun, '')) AS roadAddress, ROUND(pi.property_rating, 1) AS propertyRating, img.image_url AS imageUrl FROM property_infos pi
LEFT JOIN ( SELECT apt_seq, MAX(image_url) AS image_url FROM property_image WHERE is_main = 1 GROUP BY apt_seq ) img ON img.apt_seq = pi.apt_seq
WHERE pi.sgg_cd = #{sggCd}
AND EXISTS ( SELECT 1 FROM properties p LEFT JOIN preferences pref ON pref.user_id = #{userId} WHERE p.apt_seq = pi.apt_seq
AND (pref.min_floor IS NULL OR p.floor >= pref.min_floor) AND (pref.max_floor IS NULL OR p.floor <= pref.max_floor)
AND (pref.min_area IS NULL OR p.area >= pref.min_area) AND (pref.max_area IS NULL OR p.area <= pref.max_area)
)
AND EXISTS ( SELECT 1 FROM property_preferences_tags ppt WHERE ppt.apt_seq = pi.apt_seq AND ppt.tag_id IN
#{tid}
)
ORDER BY property_rating DESC LIMIT #{limit}

SELECT tag_id, type, name FROM tags
type = #{type}
ORDER BY tag_id
